// Get all input data with error handling
const inputs = $input.all();
console.log('Total inputs received:', inputs.length);

// Safely get data with fallbacks
const goldData = inputs[0]?.json || {};
const currencyData = inputs[1]?.json || { rates: { INR: 83 } }; // Fallback currency
const stockData = inputs[2]?.json || { data: [] }; // Fallback stock data

console.log('Gold data:', goldData);
console.log('Currency data:', currencyData);
console.log('Stock data:', stockData);

// Process timestamp
const now = new Date();
const istTime = new Date(now.toLocaleString("en-US", {timeZone: "Asia/Kolkata"}));

// Extract gold rates (handle different response formats)
let goldRates = {};
if (goldData.rates) {
  goldRates = {
    gold_per_gram: goldData.rates.gold || goldData.rates.XAU || 0,
    silver_per_gram: goldData.rates.silver || goldData.rates.XAG || 0,
    platinum_per_gram: goldData.rates.platinum || goldData.rates.XPT || 0
  };
} else if (goldData.gold) {
  goldRates = {
    gold_per_gram: goldData.gold || 0,
    silver_per_gram: goldData.silver || 0,
    platinum_per_gram: goldData.platinum || 0
  };
} else {
  // Fallback rates if no data
  goldRates = {
    gold_per_gram: 6000, // Default fallback
    silver_per_gram: 75,
    platinum_per_gram: 2500
  };
}

// Calculate different purities
const gold24k = Math.round(goldRates.gold_per_gram || 6000);
const gold22k = Math.round((goldRates.gold_per_gram || 6000) * 0.916);
const gold18k = Math.round((goldRates.gold_per_gram || 6000) * 0.75);

// Get previous rates from memory (persistent across executions)
const previousData = $vars.previousGoldData || {
  gold24k: gold24k,
  timestamp: istTime.toISOString(),
  dailyHigh: gold24k,
  dailyLow: gold24k
};

// Calculate changes
const change24h = gold24k - (previousData.gold24k || gold24k);
const changePercent = previousData.gold24k ? ((change24h / previousData.gold24k) * 100) : 0;

// Update daily high/low
const dailyHigh = Math.max(previousData.dailyHigh || gold24k, gold24k);
const dailyLow = Math.min(previousData.dailyLow || gold24k, gold24k);

// Market status
const marketHours = getMarketStatus(istTime);

// Process stock data safely
const stockInfo = processStockData(stockData);

// Create comprehensive data object
const processedData = {
  timestamp: istTime.toISOString(),
  market_session: marketHours,
  rates: {
    gold_24k_per_gram: gold24k,
    gold_22k_per_gram: gold22k,
    gold_18k_per_gram: gold18k,
    silver_per_gram: Math.round(goldRates.silver_per_gram || 75),
    platinum_per_gram: Math.round(goldRates.platinum_per_gram || 2500)
  },
  price_movement: {
    change_24h: Math.round(change24h * 100) / 100,
    change_percent_24h: Math.round(changePercent * 100) / 100,
    daily_high: dailyHigh,
    daily_low: dailyLow,
    volatility: Math.abs(changePercent) > 1 ? 'HIGH' : Math.abs(changePercent) > 0.5 ? 'MEDIUM' : 'LOW'
  },
  market_data: {
    usd_inr: (currencyData.rates && currencyData.rates.INR) ? currencyData.rates.INR : 83,
    stock_sentiment: stockInfo.sentiment,
    nifty_change: stockInfo.niftyChange
  },
  data_quality: {
    gold_data_source: 'metals.dev',
    last_updated: goldData.timestamp || istTime.toISOString(),
    api_status: goldData.status || 'unknown',
    inputs_received: inputs.length
  }
};

// Store current data for next comparison
$vars.previousGoldData = {
  gold24k: gold24k,
  timestamp: istTime.toISOString(),
  dailyHigh: dailyHigh,
  dailyLow: dailyLow
};

// Helper functions
function getMarketStatus(time) {
  const hour = time.getHours();
  const day = time.getDay();

  if (day === 0) return 'CLOSED_SUNDAY';
  if (hour >= 9 && hour <= 18) return 'ACTIVE_HOURS';
  if (hour >= 19 && hour <= 21) return 'EVENING_TRADING';
  return 'AFTER_HOURS';
}

function processStockData(stocks) {
  try {
    if (!stocks || !stocks.data || !Array.isArray(stocks.data)) {
      return { sentiment: 'NEUTRAL', niftyChange: 0 };
    }

    const niftyData = stocks.data.find(s => s.symbol === 'NIFTY 50' || s.identifier === 'NIFTY 50');
    const change = niftyData ? (niftyData.pChange || 0) : 0;

    return {
      sentiment: change > 1 ? 'POSITIVE' : change < -1 ? 'NEGATIVE' : 'NEUTRAL',
      niftyChange: Math.round(change * 100) / 100
    };
  } catch (error) {
    console.error('Error processing stock data:', error);
    return { sentiment: 'UNKNOWN', niftyChange: 0 };
  }
}

console.log('Final processed data:', processedData);

return [{ json: processedData }];

const data = $input.first().json;
const currentGold22k = data.rates.gold_22k_per_gram;
const currentGold24k = data.rates.gold_24k_per_gram;

console.log('Analyzing investment for Gold 22K:', currentGold22k);

// Investment parameters (you can customize these)
const targetPrice = 6000; // Target price per gram for 22k
const monthlyBudget = 5000;
const riskTolerance = 'MODERATE'; // LOW, MODERATE, HIGH

// Advanced analysis
const analysis = {
  market_assessment: assessMarketCondition(data),
  investment_opportunity: calculateOpportunity(currentGold22k, targetPrice, data),
  risk_analysis: assessRisk(data),
  timing_analysis: analyzeTimingFactors(data),
  recommendation: null, // Will be set below
  action_plan: null,    // Will be set below
  alerts: null          // Will be set below
};

// Generate recommendation after assessments
analysis.recommendation = generateSmartRecommendation(currentGold22k, targetPrice, data, monthlyBudget, analysis);
analysis.action_plan = createActionPlan(currentGold22k, targetPrice, data, analysis.recommendation);
analysis.alerts = generateAlerts(currentGold22k, targetPrice, data);

console.log('Investment recommendation:', analysis.recommendation.action);
console.log('Confidence level:', analysis.recommendation.confidence);

function assessMarketCondition(marketData) {
  const volatility = marketData.price_movement.volatility;
  const change = marketData.price_movement.change_percent_24h;
  const stockSentiment = marketData.market_data.stock_sentiment;

  let score = 50; // Base score

  // Price trend scoring
  if (change <= -1) score += 30; // Falling prices good for buyers
  else if (change <= -0.5) score += 20;
  else if (change >= 1) score -= 20; // Rising prices bad for buyers
  else if (change >= 2) score -= 30;

  // Volatility scoring
  if (volatility === 'LOW') score += 10; // Stable market
  else if (volatility === 'HIGH') score -= 15; // Risky market

  // Stock market correlation
  if (stockSentiment === 'NEGATIVE') score += 15; // Usually gold rises when stocks fall
  else if (stockSentiment === 'POSITIVE') score -= 10;

  // Market session bonus
  if (marketData.market_session === 'ACTIVE_HOURS') score += 5;

  const condition = score >= 70 ? 'EXCELLENT' : 
                   score >= 55 ? 'GOOD' : 
                   score >= 40 ? 'AVERAGE' : 'POOR';

  return {
    overall_score: Math.max(0, Math.min(100, score)),
    condition: condition,
    reasoning: generateMarketReasoning(score, change, volatility, stockSentiment),
    factors: {
      price_trend: change <= -1 ? 'FAVORABLE' : change >= 1 ? 'UNFAVORABLE' : 'NEUTRAL',
      volatility_impact: volatility,
      stock_correlation: stockSentiment,
      timing: marketData.market_session
    }
  };
}

function calculateOpportunity(currentRate, targetRate, data) {
  const priceDiff = currentRate - targetRate;
  const percentFromTarget = (priceDiff / targetRate) * 100;

  // Base opportunity scoring
  let opportunityScore = 0;
  if (currentRate <= targetRate) opportunityScore = 100;
  else if (currentRate <= targetRate + 50) opportunityScore = 85;
  else if (currentRate <= targetRate + 100) opportunityScore = 70;
  else if (currentRate <= targetRate + 150) opportunityScore = 50;
  else if (currentRate <= targetRate + 200) opportunityScore = 30;
  else opportunityScore = 15;

  // Adjust for market conditions
  const change = data.price_movement.change_percent_24h;
  if (change <= -1.5) opportunityScore += 15; // Big drop bonus
  else if (change <= -1) opportunityScore += 10;
  else if (change >= 1.5) opportunityScore -= 15; // Rising price penalty

  if (data.price_movement.volatility === 'HIGH') opportunityScore -= 10;
  else if (data.price_movement.volatility === 'LOW') opportunityScore += 5;

  return {
    score: Math.min(100, Math.max(0, opportunityScore)),
    current_rate: currentRate,
    target_rate: targetRate,
    difference: Math.round(priceDiff),
    percent_from_target: Math.round(percentFromTarget * 100) / 100,
    opportunity_level: opportunityScore >= 85 ? 'EXCELLENT' : 
                      opportunityScore >= 70 ? 'GOOD' : 
                      opportunityScore >= 50 ? 'MODERATE' : 
                      opportunityScore >= 30 ? 'POOR' : 'AVOID',
    buy_signal_strength: opportunityScore >= 85 ? 'STRONG_BUY' :
                        opportunityScore >= 70 ? 'BUY' :
                        opportunityScore >= 50 ? 'HOLD' : 'AVOID'
  };
}

function assessRisk(data) {
  const volatility = data.price_movement.volatility;
  const change24h = Math.abs(data.price_movement.change_percent_24h);
  const marketSession = data.market_session;

  let riskLevel = 'LOW';
  let riskScore = 20; // Base risk score
  let riskFactors = [];

  // Volatility risk
  if (volatility === 'HIGH') {
    riskScore += 40;
    riskFactors.push('High price volatility detected');
  } else if (volatility === 'MEDIUM') {
    riskScore += 20;
    riskFactors.push('Moderate price volatility');
  }

  // Price movement risk
  if (change24h > 3) {
    riskScore += 30;
    riskFactors.push('Extreme price movement in last 24h');
  } else if (change24h > 2) {
    riskScore += 20;
    riskFactors.push('Significant price movement in last 24h');
  }

  // Market timing risk
  if (marketSession === 'AFTER_HOURS') {
    riskScore += 10;
    riskFactors.push('After market hours - limited liquidity');
  }

  // Stock market correlation risk
  if (data.market_data.stock_sentiment === 'NEGATIVE') {
    riskFactors.push('Negative stock sentiment may affect gold prices');
  }

  // Determine risk level
  if (riskScore >= 70) riskLevel = 'HIGH';
  else if (riskScore >= 40) riskLevel = 'MEDIUM';
  else riskLevel = 'LOW';

  return {
    level: riskLevel,
    score: Math.min(100, riskScore),
    factors: riskFactors,
    recommendation: riskLevel === 'HIGH' ? 'Consider smaller investment amounts or wait' :
                   riskLevel === 'MEDIUM' ? 'Proceed with caution and standard amounts' : 
                   'Normal investment approach recommended',
    max_recommended_percentage: riskLevel === 'HIGH' ? 50 : riskLevel === 'MEDIUM' ? 75 : 100
  };
}

function analyzeTimingFactors(data) {
  const hour = new Date(data.timestamp).getHours();
  const session = data.market_session;

  let timingScore = 50; // Base score
  let factors = [];

  // Time of day analysis
  if (hour >= 10 && hour <= 16) {
    timingScore += 15;
    factors.push('Optimal trading hours (10 AM - 4 PM)');
  } else if (hour >= 9 && hour <= 18) {
    timingScore += 10;
    factors.push('Good trading hours');
  } else if (hour >= 20 || hour <= 8) {
    timingScore -= 10;
    factors.push('Off-market hours - prices may be stale');
  }

  // Market session analysis
  if (session === 'ACTIVE_HOURS') {
    timingScore += 15;
    factors.push('Active market session');
  } else if (session === 'EVENING_TRADING') {
    timingScore += 5;
    factors.push('Evening trading session');
  } else if (session === 'CLOSED_SUNDAY') {
    timingScore -= 20;
    factors.push('Market closed - weekend pricing');
  }

  // Day of week consideration
  const dayOfWeek = new Date(data.timestamp).getDay();
  if (dayOfWeek >= 1 && dayOfWeek <= 5) {
    timingScore += 5;
    factors.push('Weekday trading');
  }

  return {
    score: Math.max(0, Math.min(100, timingScore)),
    factors: factors,
    best_time_to_buy: timingScore >= 70 ? 'EXCELLENT_TIME' : 
                     timingScore >= 60 ? 'GOOD_TIME' : 
                     timingScore >= 40 ? 'AVERAGE_TIME' : 'POOR_TIME',
    timing_recommendation: timingScore >= 60 ? 'Act now if other factors align' :
                          timingScore >= 40 ? 'Timing is neutral' :
                          'Consider waiting for better timing'
  };
}

function generateSmartRecommendation(currentRate, targetRate, data, budget, analysis) {
  const opportunity = analysis.investment_opportunity;
  const risk = analysis.risk_analysis;
  const timing = analysis.timing_analysis;
  const market = analysis.market_assessment;

  let action = 'HOLD';
  let confidence = 50;
  let amount = 0;
  let urgency = 'LOW';
  let reasoning = '';

  // Calculate composite score
  const compositeScore = (
    opportunity.score * 0.4 +
    market.overall_score * 0.3 +
    timing.score * 0.2 +
    (100 - risk.score) * 0.1
  );

  console.log('Composite investment score:', compositeScore);

  // Decision logic based on composite score and individual factors
  if (opportunity.score >= 85 && risk.level !== 'HIGH' && compositeScore >= 75) {
    action = 'BUY_NOW';
    confidence = Math.min(95, compositeScore);
    amount = Math.round(budget * (risk.max_recommended_percentage / 100));
    urgency = 'HIGH';
    reasoning = `Excellent opportunity with ${opportunity.opportunity_level} rating and ${market.condition} market conditions`;

  } else if (opportunity.score >= 70 && compositeScore >= 65) {
    action = 'BUY_GRADUALLY';
    confidence = Math.min(85, compositeScore);
    amount = Math.round(budget * 0.7 * (risk.max_recommended_percentage / 100));
    urgency = 'MEDIUM';
    reasoning = `Good opportunity with manageable risk levels`;

  } else if (currentRate >= targetRate + 200 && risk.level === 'HIGH') {
    action = 'AVOID';
    confidence = 90;
    amount = 0;
    urgency = 'NONE';
    reasoning = `Price too high (‚Çπ${currentRate - targetRate} above target) with high risk`;

  } else if (opportunity.score >= 50 && risk.level === 'LOW') {
    action = 'SMALL_BUY';
    confidence = Math.min(75, compositeScore);
    amount = Math.round(budget * 0.4);
    urgency = 'LOW';
    reasoning = `Moderate opportunity with low risk - conservative approach`;

  } else {
    action = 'WAIT_AND_MONITOR';
    confidence = 60;
    amount = Math.round(budget * 0.2);
    urgency = 'LOW';
    reasoning = `Mixed signals - wait for clearer opportunity`;
  }

  const gramsCanBuy = amount > 0 ? (amount / currentRate) : 0;

  return {
    action: action,
    confidence: Math.round(confidence),
    urgency: urgency,
    recommended_amount: amount,
    grams_you_can_buy: Math.round(gramsCanBuy * 1000) / 1000,
    reasoning: reasoning,
    composite_score: Math.round(compositeScore),
    investment_grade: compositeScore >= 80 ? 'A+' :
                     compositeScore >= 70 ? 'A' :
                     compositeScore >= 60 ? 'B' :
                     compositeScore >= 50 ? 'C' : 'D',
    alternative_strategy: generateAlternativeStrategy(currentRate, targetRate, budget, risk.level)
  };
}

function createActionPlan(currentRate, targetRate, data, recommendation) {
  const actions = [];

  if (recommendation.action === 'BUY_NOW') {
    actions.push({
      priority: 'HIGH',
      task: 'Execute gold purchase immediately',
      timeline: 'Within 2-4 hours',
      amount: `‚Çπ${recommendation.recommended_amount}`,
      expected_grams: `${recommendation.grams_you_can_buy}g`,
      details: 'Check 2-3 local dealers for best rates'
    });
    actions.push({
      priority: 'MEDIUM',
      task: 'Verify current market rates',
      timeline: 'Before purchase',
      potential_savings: '‚Çπ20-50 per gram',
      details: 'Compare online vs physical store rates'
    });

  } else if (recommendation.action === 'BUY_GRADUALLY') {
    actions.push({
      priority: 'MEDIUM',
      task: 'Start gradual investment plan',
      timeline: 'This week',
      amount: `‚Çπ${Math.round(recommendation.recommended_amount / 2)} now, rest in 1 week`,
      strategy: 'Rupee cost averaging'
    });

  } else if (recommendation.action === 'WAIT_AND_MONITOR') {
    actions.push({
      priority: 'MEDIUM',
      task: 'Set up price alerts',
      timeline: 'Today',
      target: `‚Çπ${targetRate} or below`,
      benefit: 'Get notified when opportunity improves'
    });
    actions.push({
      priority: 'LOW',
      task: 'Review monthly budget allocation',
      timeline: 'This week',
      benefit: 'Prepare for next opportunity'
    });
  }

  // Always add monitoring task
  actions.push({
    priority: 'LOW',
    task: 'Monitor market trends',
    timeline: 'Daily',
    focus: 'Watch for significant price movements or market changes'
  });

  return actions;
}

function generateAlerts(currentRate, targetRate, data) {
  const alerts = [];
  const change = data.price_movement.change_percent_24h;

  // Target price reached alert
  if (currentRate <= targetRate) {
    alerts.push({
      type: 'OPPORTUNITY',
      severity: 'HIGH',
      title: 'üéØ TARGET PRICE REACHED!',
      message: `Gold 22K at ‚Çπ${currentRate} - At/below your target of ‚Çπ${targetRate}!`,
      action_required: true,
      expected_action: 'Consider immediate purchase'
    });
  }

  // Excellent deal alert
  if (currentRate <= targetRate - 100) {
    alerts.push({
      type: 'RARE_OPPORTUNITY',
      severity: 'URGENT',
      title: 'üî• RARE OPPORTUNITY!',
      message: `Gold ‚Çπ${Math.abs(currentRate - targetRate)} below target! Exceptional deal!`,
      action_required: true,
      expected_action: 'Buy maximum affordable amount'
    });
  }

  // Significant price drop alert
  if (change <= -2) {
    alerts.push({
      type: 'PRICE_DROP',
      severity: 'MEDIUM',
      title: 'üìâ SIGNIFICANT PRICE DROP',
      message: `Gold dropped by ${Math.abs(change).toFixed(1)}% in 24h. Buying opportunity!`,
      action_required: false,
      trend: 'FAVORABLE'
    });
  }

  // High volatility warning
  if (data.price_movement.volatility === 'HIGH') {
    alerts.push({
      type: 'VOLATILITY',
      severity: 'MEDIUM',
      title: '‚ö†Ô∏è HIGH VOLATILITY DETECTED',
      message: 'Gold prices showing high volatility. Exercise caution with large purchases.',
      action_required: false,
      recommendation: 'Consider smaller investment amounts'
    });
  }

  // Overpriced warning
  if (currentRate >= targetRate + 300) {
    alerts.push({
      type: 'OVERPRICED',
      severity: 'LOW',
      title: 'üí∏ SIGNIFICANTLY OVERPRICED',
      message: `Gold at ‚Çπ${currentRate} is ‚Çπ${currentRate - targetRate} above target. Consider waiting.`,
      action_required: false,
      recommendation: 'Wait for price correction'
    });
  }

  return alerts;
}

// Helper function implementations
function generateMarketReasoning(score, change, volatility, stockSentiment) {
  let reasons = [];
  if (change <= -1) reasons.push('prices falling (favorable for buyers)');
  else if (change >= 1) reasons.push('prices rising (unfavorable for buyers)');

  if (volatility === 'LOW') reasons.push('stable market conditions');
  else if (volatility === 'HIGH') reasons.push('high volatility (risky)');

  if (stockSentiment === 'NEGATIVE') reasons.push('negative stock sentiment (often good for gold)');
  else if (stockSentiment === 'POSITIVE') reasons.push('positive stock sentiment (may reduce gold demand)');

  return reasons.length > 0 ? reasons.join(', ') : 'mixed market signals';
}

function generateAlternativeStrategy(currentRate, targetRate, budget, riskLevel) {
  if (currentRate > targetRate + 150) {
    return {
      strategy: 'RUPEE_COST_AVERAGING',
      description: 'Invest smaller amounts weekly instead of lump sum',
      weekly_amount: Math.round(budget / 4),
      duration: '4 weeks',
      benefit: 'Reduces impact of price volatility and timing risk'
    };
  } else if (riskLevel === 'HIGH') {
    return {
      strategy: 'WAIT_AND_WATCH',
      description: 'Monitor for 1-2 weeks before investing',
      monitoring_frequency: 'Daily',
      trigger_conditions: [`Price drops below ‚Çπ${targetRate + 50}`, 'Volatility decreases'],
      benefit: 'Avoid buying during high-risk periods'
    };
  }
  return null;
}

// Combine original data with analysis
const finalData = {
  ...data,
  investment_analysis: analysis,
  analysis_timestamp: new Date().toISOString(),
  analysis_version: '2.0'
};

console.log('Analysis complete. Final recommendation:', analysis.recommendation.action);

return [{ json: finalData }];

